import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const AIToolDetails = () => {
  const [tool, setTool] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    fetchTool();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  const fetchTool = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/ai-tools/${id}`);
      setTool(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  if (!tool || !tool.name || !tool.description || !tool.link || !tool.category || !tool.department || !tool.use) {
    return (
      <div className="loading-animation-container">
        <div className="loading-animation">
          <div></div>
          <div></div>
          <div></div>
          <div></div>
        </div>
      </div>
    );
  }

  return (
    <div className="ai-tool-details ai-tool-3d-card">
      <div className="ai-tool-3d-card-inner">
        <div className="ai-tool-details-header">
          <div className="ai-tool-details-header-left">
            <h2>{tool.name}</h2>
          </div>
          <div className="ai-tool-details-header-right">
            <a 
              href={tool.link} 
              target="_blank" 
              rel="noopener noreferrer" 
              className="read-more-button"
            >
              Visit Website
            </a>
          </div>
        </div>
        
        <div className="tool-details-content">
          <p><strong>Description:</strong> {tool.description}</p>
          <div className="tool-metadata">
            <p>
              <span className="metadata-label">Category:</span> 
              <span className="department-label">{tool.category}</span>
            </p>
            <p>
              <span className="metadata-label">Department:</span> 
              <span className="department-label">{tool.department}</span>
            </p>
            <p><strong>Use:</strong> {tool.use}</p>
          </div>
        </div>

        <div className="know-more-container">
          <button 
            className="know-more-button"
            onClick={() => window.open(
              `/tool-details/${tool._id}?name=${encodeURIComponent(tool.name)}&description=${encodeURIComponent(tool.description)}&link=${encodeURIComponent(tool.link)}&category=${encodeURIComponent(tool.category || '')}&department=${encodeURIComponent(tool.department || '')}&use=${encodeURIComponent(tool.use || '')}&details=${encodeURIComponent(tool.details || '')}`, 
              '_blank'
            )}
          >
            Know More
          </button>
        </div>
      </div>
    </div>
  );
};

export default AIToolDetails;
