import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const AIToolList = () => {
  const [aiTools, setAiTools] = useState([]);

  useEffect(() => {
    fetchAiTools();
  }, []);

  const fetchAiTools = async () => {
    try {
      const response = await axios.get('http://localhost:3000/ai-tools');
      setAiTools(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h2>AI Tools</h2>
      <ul className="ai-tool-list">
        {aiTools
          .filter(tool => tool.name && tool.description && tool.link)
          .map((tool) => (
            <li key={tool._id}>
              <a href={tool.link} target="_blank" rel="noopener noreferrer">
                {tool.name}
              </a>
              <p>Category: {tool.category}</p>
              <Link to={{
                pathname: `/tool/${tool._id}`,
                state: { tool }
              }} style={{
                backgroundColor: '#f0f4f8',
                color: '#2d3436',
                fontWeight: 'bold',
                padding: '5px 10px',
                borderRadius: '5px',
                border: 'none',
                cursor: 'pointer',
                textDecoration: 'none'
              }}>Read More</Link>
            </li>
          ))}
        </ul>
    </div>
  );
};

export default AIToolList;
