import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useLocation } from 'react-router-dom';

const AIToolDetailsPage = () => {
  const [tool, setTool] = useState(null);
  const { id } = useParams();
  const location = useLocation();
  const details = new URLSearchParams(location.search).get('details');

  useEffect(() => {
    fetchTool();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  const fetchTool = async () => {
    try {
      const response = await axios.get(`http://localhost:3000/ai-tools/${id}`);
      setTool(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  if (!tool) {
    return <div>Loading...</div>;
  }

  return (
    <div className="ai-tool-details-page">
      <h2>{tool.name}</h2>
      <p>Description: {tool.description}</p>
      <p>Category: {tool.category}</p>
      <p>Department: {tool.department}</p>
      <p>Use: {tool.use}</p>
      <p>Details: {details}</p>
      <a href={tool.link} target="_blank" rel="noopener noreferrer">
        Visit Website
      </a>
    </div>
  );
};

export default AIToolDetailsPage;
