/* eslint-disable jsx-a11y/iframe-has-title */
import './App.css';
import AIToolList from './components/AIToolList';
import AIToolDetails from './components/AIToolDetails';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import { useState, useCallback } from 'react';

function App() {
  const [categories, setCategories] = useState([]);

  const fetchCategories = useCallback(async () => {
    const aiToolList = new AIToolList();
    const tools = await aiToolList.fetchAiTools();
    const uniqueCategories = [...new Set(tools.map(tool => tool.category))];
    setCategories(uniqueCategories);
  }, []);

  return (
    <BrowserRouter>
      <div className="App" style={{ overflow: 'hidden', height: '100vh' }}>
        <iframe
          src='https://my.spline.design/robotfollowcursorforlandingpage-9f106b3356a92dcc74818c539de79db7/'
          frameBorder='0'
          width='100%'
          height='100%'
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            zIndex: -1,
          }}
        />
        <nav className="navbar">
          <h1>AI Tools App</h1>
          <div >
            <a href="/">Home</a>
            <div style={{position: 'relative'}}>
              <a href="/categories"
                 onMouseEnter={fetchCategories}
              >Categories</a>
              {categories.length > 0 && (
                <div style={{position: 'absolute', top: '100%', left: 0, backgroundColor: 'white', border: '1px solid #ccc'}}>
                  {categories.map(category => (
                    <a key={category} href={`/categories/${category}`}>{category}</a>
                  ))}
                </div>
              )}
            </div>
          </div>
        </nav>
        <header className="App-header">
          <div className="hero">
          </div>
          <Routes>
            <Route path="/" element={<AIToolList />} />
            <Route path="/tool/:id" element={<AIToolDetails />} />
          </Routes>
        </header>
      </div>
    </BrowserRouter>
  );
}

export default App;
