import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const AIToolList = ({setCategories}) => {
  const [aiTools, setAiTools] = useState([]);

  const fetchAiTools = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:3000/ai-tools');
      setAiTools(response.data);
      const uniqueCategories = [...new Set(response.data.map(tool => tool.category))];
      setCategories(uniqueCategories);
      return response.data;
    } catch (error) {
      console.error(error);
      return [];
    }
  }, [setCategories]);

  useEffect(() => {
    fetchAiTools();
  }, [fetchAiTools]);

  return (
    <div>
      <h2>AI Tools</h2>
      {aiTools.length > 0 ? (
        <ul className="ai-tool-list">
          {aiTools
            .filter(tool => tool.name && tool.description && tool.link)
            .map((tool) => (
              <li key={tool._id}>
                <a href={tool.link} target="_blank" rel="noopener noreferrer">
                  {tool.name}
                </a>
                <p>Category: {tool.category}</p>
                <button style={{
                  backgroundColor: '#f0f4f8',
                  color: '#2d3436',
                  fontWeight: 'bold',
                  padding: '5px 10px',
                  borderRadius: '5px',
                  border: 'none',
                  cursor: 'pointer'
                }} onClick={() => window.open(`/tool/${tool._id}?name=${encodeURIComponent(tool.name)}&description=${encodeURIComponent(tool.description)}&link=${encodeURIComponent(tool.link)}&category=${encodeURIComponent(tool.category || '')}&department=${encodeURIComponent(tool.department || '')}&use=${encodeURIComponent(tool.use || '')}`, '_blank')}>Read More</button>
              </li>
            ))}
        </ul>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
};

export default AIToolList;
