import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const AIToolDetailsPage = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [link, setLink] = useState('');
  const [category, setCategory] = useState('');
  const [department, setDepartment] = useState('');
  const [use, setUse] = useState('');
  const [details, setDetails] = useState('');
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    setName(params.get('name') || '');
    setDescription(params.get('description') || '');
    setLink(params.get('link') || '');
    setCategory(params.get('category') || '');
    setDepartment(params.get('department') || '');
    setUse(params.get('use') || '');
    setDetails(params.get('details') || '');
  }, [location]);

  return (
    <div className="ai-tool-details">
      <div className="ai-tool-details-header">
        <div className="ai-tool-details-header-left">
          <h2>{name}</h2>
        </div>
        <div className="ai-tool-details-header-right">
          <a 
            href={link} 
            target="_blank" 
            rel="noopener noreferrer" 
            className="read-more-button"
          >
            Visit Website
          </a>
        </div>
      </div>
      
      <div className="tool-details-content">
        <p><strong>Description:</strong> {description}</p>
        <div className="tool-metadata">
          <p>
            <span className="metadata-label">Category:</span> 
            <span className="department-label">{category}</span>
          </p>
          <p>
            <span className="metadata-label">Department:</span> 
            <span className="department-label">{department}</span>
          </p>
          <p><strong>Use:</strong> {use}</p>
          {details && (
            <p><strong>Additional Details:</strong> {details}</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default AIToolDetailsPage;
