import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useLocation } from 'react-router-dom';
import './AIToolDetailsPage.css';

const AIToolDetailsPage = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [link, setLink] = useState('');
  const [category, setCategory] = useState('');
  const [department, setDepartment] = useState('');
  const [use, setUse] = useState('');
  const [details, setDetails] = useState('');
  const { id } = useParams();
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    setName(params.get('name') || '');
    setDescription(params.get('description') || '');
    setLink(params.get('link') || '');
    setCategory(params.get('category') || '');
    setDepartment(params.get('department') || '');
    setUse(params.get('use') || '');
    setDetails(params.get('details') || '');
  }, [location]);

  return (
    <div className="ai-tool-details-page">
      <h2>{name}</h2>
      <p>Description: {description}</p>
      <p>Category: {category}</p>
      <p>Department: {department}</p>
      <p>Use: {use}</p>
      <p>Details: {details}</p>
      <a href={link} target="_blank" rel="noopener noreferrer">
        Visit Website
      </a>
    </div>
  );
};

export default AIToolDetailsPage;
